import React, { useState } from 'react'

import axios from 'axios'
import { useNavigate } from 'react-router-dom'

function CreateProduct() {
    const [name, setName] = useState('')
    const [number, setNumber] = useState(0)
    const [category, setCategory] = useState('')
    const [description, setDescription] = useState('')


    const navigate = useNavigate()

    const handleSubmit = (e) => {
        e.preventDefault()
        axios.post('http://localhost:5000/createProduct', {name, number, category, description})
        .then(result => {
            navigate('/')
            console.log(result)
        })
    }   
    

  return (
    <div className='w-full py-12'>
      <form onSubmit={handleSubmit} className='flex flex-col gap-4 container mx-auto w-[400px]'>
        <input className='p-2' type="text" placeholder='Product name' onChange={(e) => setName(e.target.value)} />
        <input className='p-2' type="number" placeholder='Product price' onChange={(e) => setNumber(e.target.value)}/>
        <input className='p-2' type="text" placeholder='Product category' onChange={(e) => setCategory(e.target.value)}/>
        <input className='p-2' type="text" placeholder='Product description'onChange={(e) => setDescription(e.target.value)} />

        <button className='w-full bg-lime-500 p-2'>Add item</button>
      </form>
    </div>
  )
}

export default CreateProduct
